$(function(){$(".sb--facebook").each(function(e,t){var n=$(t),a=n.data("url");a&&n.click(function(e){e.preventDefault();var t="https://www.facebook.com/sharer/sharer.php?u="+encodeURIComponent(a);window.open(t,"_blank","width=600,height=460")})}),$(".sb--twitter").each(function(e,t){var n=$(t),a=n.data("url"),o=n.data("via"),c=n.data("text")||"";a&&n.click(function(e){e.preventDefault();var t="https://twitter.com/intent/tweet?via="+encodeURIComponent(o)+"&url="+encodeURIComponent(a)+"&text="+encodeURIComponent(c);window.open(t,"_blank","width=600,height=460")})}),$(".sb--pinterest").each(function(e,t){var n=$(t),a=n.data("url"),o=n.data("media");a&&n.click(function(e){e.preventDefault();var t="https://www.pinterest.com/pin/create/link/?url="+encodeURIComponent(a)+"&media="+encodeURIComponent(o);window.open(t,"_blank","width=400,height=460")})}),$(".sb--mail").each(function(e,t){var n=$(t),a=n.data("url"),o=n.data("subject")||"";a&&n.click(function(e){e.preventDefault();var t="mailto:?subject="+encodeURIComponent(o)+"&body="+encodeURIComponent(a);window.open(t,"_blank")})}),$(".sb--whatsapp").each(function(e,t){var n=$(t),a=n.data("url"),o=n.data("text")||"";a&&n.click(function(e){e.preventDefault();var t="whatsapp://send?text="+encodeURIComponent(a)+"%20"+encodeURIComponent(o);window.open(t,"_blank")})})});