$(function(){$(".sb--facebook").each(function(t,e){var n=$(e),o=n.data("url");o&&n.click(function(t){t.preventDefault(),FB.ui({method:"share",href:o},function(t){})})}),$(".sb--twitter").each(function(t,e){var n=$(e),o=n.data("url"),a=n.data("via"),c=n.data("text")||"";o&&n.click(function(t){t.preventDefault();var e="https://twitter.com/intent/tweet??via="+encodeURIComponent(a)+"&url="+encodeURIComponent(o)+"&text="+encodeURIComponent(c);window.open(e,"_blank","width=400,height=300")})}),$(".sb--google-plus").each(function(t,e){var n=$(e),o=n.data("url");o&&n.click(function(t){t.preventDefault();var e="https://plus.google.com/share?url="+encodeURIComponent(o);window.open(e,"_blank","width=400,height=460")})}),$(".sb--mail").each(function(t,e){var n=$(e),o=n.data("url"),a=n.data("subject")||"";o&&n.click(function(t){t.preventDefault();var e="mailto:?subject="+encodeURIComponent(a)+"&body="+encodeURIComponent(o);window.open(e,"_blank")})}),$(".sb--whatsapp").each(function(t,e){var n=$(e),o=n.data("url"),a=n.data("text")||"";o&&n.click(function(t){t.preventDefault();var e="whatsapp://send?text="+encodeURIComponent(o)+"%20"+encodeURIComponent(a);window.open(e,"_blank")})})});