$(function(){$(".sb--facebook").each(function(t,e){var n=$(e),o=n.data("url");o&&n.click(function(t){t.preventDefault(),FB.ui({method:"share",href:o},function(t){})})}),$(".sb--twitter").each(function(t,e){var n=$(e),o=n.data("url"),a=n.data("via");o&&n.click(function(t){t.preventDefault();var e="https://twitter.com/intent/tweet?text="+encodeURIComponent(o)+" via @"+a;window.open(e,"_blank","width=400,height=300")})}),$(".sb--google-plus").each(function(t,e){var n=$(e),o=n.data("url");o&&n.click(function(t){t.preventDefault();var e="https://plus.google.com/share?url="+encodeURIComponent(o);window.open(e,"_blank","width=400,height=460")})})});