$(document).on("init-share-buttons",function(){$(this).trigger("reset-share-buttons"),$(".sb--facebook").each(function(t,e){var n=$(e),o=n.data("url");o&&n.click(function(t){t.preventDefault();var e="https://www.facebook.com/sharer/sharer.php?u="+encodeURIComponent(o);window.open(e,"_blank","width=600,height=460")})}),$(".sb--twitter").each(function(t,e){var n=$(e),o=n.data("url"),a=n.data("via"),i=n.data("text")||"";o&&n.click(function(t){t.preventDefault();var e="https://twitter.com/intent/tweet?url="+encodeURIComponent(o)+"&text="+encodeURIComponent(i);a&&(e+="&via="+encodeURIComponent(a)),window.open(e,"_blank","width=600,height=460")})}),$(".sb--pinterest").each(function(t,e){var n=$(e),o=n.data("url"),a=n.data("media");o&&n.click(function(t){t.preventDefault();var e="https://www.pinterest.com/pin/create/link/?url="+encodeURIComponent(o)+"&media="+encodeURIComponent(a);window.open(e,"_blank","width=400,height=460")})}),$(".sb--mail").each(function(t,e){var n=$(e),o=n.data("url"),a=n.data("subject")||"";o&&n.click(function(t){t.preventDefault();var e="mailto:?subject="+encodeURIComponent(a)+"&body="+encodeURIComponent(o);window.open(e,"_blank")})}),$(".sb--whatsapp").each(function(t,e){var n=$(e),o=n.data("url"),a=n.data("text")||"";o&&n.click(function(t){t.preventDefault();var e="whatsapp://send?text="+encodeURIComponent(o)+"%20"+encodeURIComponent(a);window.open(e,"_blank")})})}).on("reset-share-buttons",function(){$(".sb--facebook, .sb--twitter, .sb--pinterest, .sb--mail, .sb--whatsapp").each(function(t,e){$(e).off("click")})}).trigger("init-share-buttons");