$(function(){$(".sb--facebook").each(function(e,t){var n=$(t),o=n.data("url");o&&n.click(function(e){e.preventDefault(),FB.ui({method:"share",href:o},function(e){})})}),$(".sb--twitter").each(function(e,t){var n=$(t),o=n.data("url"),a=n.data("via"),c=n.data("text")||"";o&&n.click(function(e){e.preventDefault();var t="https://twitter.com/intent/tweet??via="+encodeURIComponent(a)+"&url="+encodeURIComponent(o)+"&text="+encodeURIComponent(c);window.open(t,"_blank","width=400,height=300")})}),$(".sb--google-plus").each(function(e,t){var n=$(t),o=n.data("url");o&&n.click(function(e){e.preventDefault();var t="https://plus.google.com/share?url="+encodeURIComponent(o);window.open(t,"_blank","width=400,height=460")})}),$(".sb--pinterest").each(function(e,t){var n=$(t),o=n.data("url"),a=n.data("media");o&&n.click(function(e){e.preventDefault();var t="https://www.pinterest.com/pin/create/link/?url="+encodeURIComponent(o)+"&media="+encodeURIComponent(a);window.open(t,"_blank","width=400,height=460")})}),$(".sb--mail").each(function(e,t){var n=$(t),o=n.data("url"),a=n.data("subject")||"";o&&n.click(function(e){e.preventDefault();var t="mailto:?subject="+encodeURIComponent(a)+"&body="+encodeURIComponent(o);window.open(t,"_blank")})}),$(".sb--whatsapp").each(function(e,t){var n=$(t),o=n.data("url"),a=n.data("text")||"";o&&n.click(function(e){e.preventDefault();var t="whatsapp://send?text="+encodeURIComponent(o)+"%20"+encodeURIComponent(a);window.open(t,"_blank")})})});